package basicsofprogramming;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float a = 13.3f;
		float b = 23.5f;
		float sum = a + b;
		
		double num1 = 50;
		double num2 = 20.5;
		double difference = num1 - num2;
		
		System.out.println("Sum of 2 number = " + sum);
		System.out.println("Difference of 2 number = " + difference);
		
		String name = "Ashritha";
		int len = name.length();
		System.out.println("Length of " + name + " is " + len);
		
	}
}
