package basicsofprogramming;

public class LoopsExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		int i = 1;
		while(i<=10)
		{
			System.out.println(i);
			i++;
		}
		
		for(int j=1;j<=10;j++)
		{
			System.out.println(j);
		}
		
		int k = 1;
		do 
		{
			System.out.println(k);
			k++;
		}while(k<=10);
	}

}
