package basicsofprogramming;

public class SubString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1 = "Ashritha Bhat";
		System.out.println("Substring of " + s1 + " is " + s1.substring(0, 8));
		System.out.println("Substring of " + s1 + " is " + s1.substring(9, 13));
	}
}
