package javaprogramming;

public class ConstructorValues {
	boolean	a;
	byte	b;
	short	c;
	int	    d;
	long	e;
	char	f;
	float	g;
	double	h;
	String i;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		ConstructorValues obj = new ConstructorValues();
		
		System.out.println("Value of a = " + obj.a);
		System.out.println("Value of b = " + obj.b);
		System.out.println("Value of c = " + obj.c);
		System.out.println("Value of d = " + obj.d);
		System.out.println("Value of e = " + obj.e);
		System.out.println("Value of f = " + obj.f);
		System.out.println("Value of g = " + obj.g);
		System.out.println("Value of h = " + obj.h);
		System.out.println("Value of h = " + obj.i);
	}

}
